mapper.update:
update sunshine_user_ssm
set user_del_flag = #{0,jdbcType=INTEGER}
where user_id = #{userId,jdbcType=INTEGER}

mapper.select:
select user_nickname as userNickname, user_dep as userDepartment ,user_phone as userPhone, user_phone2 as userPhone2,
       user_qq as userQQ, user_wechat as userWechat, user_position_chinese as userPositionChinese, user_status as userStatus
from sunshine_user_ssm_ssm where user_phone LIKE CONCAT('%',#{phone,jdbcType=VARCHAR},'%') or user_phone2 LIKE CONCAT('%',#{phone,jdbcType=VARCHAR},'%')

select user_nickname as userNickname, user_dep as userDepartment ,user_phone as userPhone, user_phone2 as userPhone2,
       user_qq as userQQ, user_wechat as userWechat, user_position_chinese as userPositionChinese, user_status as userStatus
from sunshine_user_ssm_ssm where user_qq LIKE CONCAT('%',#{userQQ,jdbcType=VARCHAR},'%')

select user_nickname as userNickname, user_dep as userDepartment ,user_phone as userPhone, user_phone2 as userPhone2,
       user_qq as userQQ, user_wechat as userWechat, user_position_chinese as userPositionChinese, user_status as userStatus
from sunshine_user_ssm_ssm where user_wechat LIKE CONCAT('%',#{userWechat,jdbcType=VARCHAR},'%')
